/*
 * https://leetcode.com/problems/is-subsequence/description/
 *
 * Given a string s and a string t, check if s is subsequence of t.
 *
 * You may assume that there is only lower case English letters in both s and
 * t. t is potentially a very long (length ~= 500,000) string, and s is a short
 * string (<=100).
 *
 * A subsequence of a string is a new string which is formed from the original
 * string by deleting some (can be none) of the characters without disturbing
 * the relative positions of the remaining characters. (ie, "ace" is a
 * subsequence of "abcde" while "aec" is not).
 */
#include <iostream>
#include <string>

using namespace std;

class Solution {
public:
    bool isSubsequence(string s, string t) {
        // s needs to be smaller than t 
        auto s_len = s.length();
        auto t_len = t.length(); 

        if (s_len > t_len)
            return false;

        auto it = begin(t);
        for (auto c: s) {
            for (it; it != end(t); ++it) {
                if (c == *it)
                    break; 
            }

            // t reach the end before s, so no
            if (it == end(t))
                return false;

            ++it;
        }
    
        return true;
    }
};

int main()
{
    Solution s;
    
    cout << s.isSubsequence("abc", "ahbgdc") << endl;
    cout << s.isSubsequence("axc", "ahbgdc") << endl;
    cout << s.isSubsequence("a", "b") << endl;
    cout << s.isSubsequence("leeeeetcode", "") << endl;

    return 0;
}
